import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [stateText, setStateText] = useState({verTexto});
  const verTexto = (k) => {
    //setStateText(!stateText);
    console.log({
      value: k.target.value
    });
  };
  return ( 
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          <h4> 
            {stateText ? "r" : "noTexto"} 
          </h4>
          <input type="text" id="introducirTexto" onKeyUp={verTexto}>
          </input>
        </div>
        <div id="verTexto" >
          <button onClick={verTexto}>ver texto</button>
        </div>
      </header>
    </div>
  );
}

export default App;
